import React, { useEffect, useState } from "react";
import { Link, useParams, useLocation } from "react-router-dom";

import classes from "./AdditDet.module.css";

const AdditionalProductDetailsPage = () => {
  const { id } = useParams();
  const location = useLocation();
  const [productDetails, setProductDetails] = useState(null);

  useEffect(() => {
    // For demonstration purposes, we'll use a timeout to simulate an asynchronous request
    const fetchProductDetails = () => {
      setTimeout(() => {
        // Check if product details were passed from AddProductForm
        if (location.state) {
          const { title, thumbnail } = location.state;
          setProductDetails({
            id,
            title,
            thumbnail,
            desc: "Description Undefined",
            price: "Price Undefined",
            discountPercentage: null,
            rating: null,
            stock: null,
            brand: null,
            category: null,
            images: [],
          });
        } else {
          // Assuming products with ID less than 30 have additional information
          if (parseInt(id) < 30) {
            fetch(`https://dummyjson.com/products/${id}`)
              .then((response) => {
                if (!response.ok) {
                  throw new Error("Network response was not ok.");
                }
                return response.json();
              })
              .then((data) => {
                setProductDetails(data);
              })
              .catch((error) => {
                console.error("Error fetching product details:", error);
              });
          } else {
            // Product with ID 30 or greater does not have additional information
            setProductDetails({
              id,
              title: "Title Undefined",
              thumbnail: "thumbnail_undefined.png",
              desc: "Description Undefined",
              price: "Price Undefined",
              discountPercentage: null,
              rating: null,
              stock: null,
              brand: null,
              category: null,
              images: [],
            });
          }
        }
      }, 500); // Simulate a delay of 500ms
    };

    fetchProductDetails();
  }, [id, location.state]);

  if (!productDetails) {
    return <p>Loading product details...</p>;
  }

  // Check if additional information is available for the product
  const hasAdditionalInfo =
    productDetails.discountPercentage !== null &&
    productDetails.rating !== null &&
    productDetails.stock !== null &&
    productDetails.brand !== null &&
    productDetails.category !== null;

  return (
    <>
      <div className={classes.product}>
        <h2>{productDetails.title}</h2>
        <img src={productDetails.thumbnail} alt="Product Thumbnail" />
        <h3>{productDetails.desc}</h3>
        <p className={classes.price}>{productDetails.price + "$"}</p>
        {hasAdditionalInfo ? (
          <ul>
            <p className={classes.additional_info}>Additional Info:</p>
            <li>
              <p>
                Discount Percentage:{" "}
                {productDetails.discountPercentage
                  ? productDetails.discountPercentage + "%"
                  : "/"}
              </p>
              <p>
                Rating: {productDetails.rating ? productDetails.rating : "/"}
              </p>
              <p>Stock: {productDetails.stock ? productDetails.stock + "pcs" : "/"}</p>
              <p>Brand: {productDetails.brand ? productDetails.brand : "/"}</p>
              <p>Category: {productDetails.category ? productDetails.category : "/"}</p>
            </li>
          </ul>
        ) : (
          <p>No additional information available for this item.</p>
        )}
        {productDetails.images &&
          productDetails.images.map((image, index) => (
            <img key={index} src={image} alt={`${index + 1}`} />
          ))}
      </div>
      <div className={classes.go_back}>
        <Link to="/">
          <button>Go Back</button>
        </Link>
      </div>
    </>
  );
};

export default AdditionalProductDetailsPage;
