import React, { useState } from "react";
import { useDispatch } from "react-redux";

import classes from "./EditForm.module.css";

const EditProductForm = ({ product, onSave }) => {
  const dispatch = useDispatch();
  const [title, setTitle] = useState(product.title);
  const [desc, setDesc] = useState(product.desc);
  const [price, setPrice] = useState(product.price);
  const [error, setError] = useState(""); // New state to hold the validation error message

  const handleEditProduct = () => {
    // Validate if all required fields are filled
    if (!title || !desc || !price) {
      setError("Please fill in all the required fields.");
      return;
    }

    const editedProduct = {
      ...product,
      title,
      desc,
      price: `${parseFloat(price).toFixed(2)}$`, // Add the $ sign after saving changes
    };
    dispatch({ type: "EDIT_PRODUCT", payload: editedProduct });
    onSave();
  };

  const handlePriceChange = (e) => {
    // Remove the $ sign before editing the price
    const inputValue = e.target.value.replace(/\$/g, "");
    const newPrice =
      inputValue === "" || /^\d+$/.test(inputValue) ? inputValue : price;
    setPrice(newPrice);
  };

  return (
    <div className={classes.form}>
      <p>Title</p>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <p>Description</p>
      <input
        type="text"
        value={desc}
        onChange={(e) => setDesc(e.target.value)}
        placeholder="Description"
      />
      <p>Price</p>
      <input
        type="text"
        value={price}
        onChange={handlePriceChange}
        placeholder="Price"
      />
      {error && <p className={classes.error}>{error}</p>} {/* Show the error message if there's any */}
      <button onClick={handleEditProduct}>Save Changes</button>
    </div>
  );
};

export default EditProductForm;
