import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

import classes from "./Products.module.css";

const ProductDetailsPage = () => {
  const { id } = useParams();
  const [productDetails, setProductDetails] = useState(null);

  useEffect(() => {
    fetch(`https://dummyjson.com/products/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        // Set the fetched product details in the state
        setProductDetails(data);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
      });
  }, [id]);

  if (!productDetails) {
    return <p>Loading product details...</p>;
  }

  return (
    <div className={classes.product}>
      <h2>{productDetails.title}</h2>
      <img src={productDetails.thumbnail} alt="Product Thumbnail" />
      <h3>{productDetails.desc}</h3>
      <p>{productDetails.price}</p>
      {/* Display additional product information */}
      <p>Discount Percentage: {productDetails.discountPercentage}</p>
      <p>Rating: {productDetails.rating}</p>
      <p>Stock: {productDetails.stock}</p>
      <p>Brand: {productDetails.brand}</p>
      <p>Category: {productDetails.category}</p>
      {/* Render the images */}
      {productDetails.images.map((image, index) => (
        <img key={index} src={image} alt={`${index + 1}`} />
      ))}
      <Link to="/">Go Back</Link>
    </div>
  );
};

export default ProductDetailsPage;
