import React, { useState } from "react";
import { useDispatch } from "react-redux";

const EditProductForm = ({ product, onSave }) => {
  const dispatch = useDispatch();
  const [title, setTitle] = useState(product.title);
  const [desc, setDesc] = useState(product.desc);
  const [price, setPrice] = useState(product.price);

  const handleEditProduct = () => {
    const editedProduct = {
      ...product,
      title,
      desc,
      price: `${parseFloat(price).toFixed(2)}$`, // Add the $ sign after saving changes
    };
    dispatch({ type: "EDIT_PRODUCT", payload: editedProduct });
    onSave();
  };

  const handlePriceChange = (e) => {
    // Remove the $ sign before editing the price
    const inputValue = e.target.value.replace(/\$/g, "");
    const newPrice = inputValue === "" || /^\d+$/.test(inputValue) ? inputValue : price;
    setPrice(newPrice);
  };

  return (
    <div>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <input
        type="text"
        value={desc}
        onChange={(e) => setDesc(e.target.value)}
        placeholder="Description"
      />
      <input
        type="text"
        value={price}
        onChange={handlePriceChange}
        placeholder="Price"
      />
      <button onClick={handleEditProduct}>Save Changes</button>
    </div>
  );
};

export default EditProductForm;
