const handleSort = (option) => {
    // If the same option is selected again, toggle the sorting order
    if (option === sortingOption) {
      setSortingOrder((prevSortingOrder) =>
        prevSortingOrder === "ascending" ? "descending" : "ascending"
      );
    } else {
      setSortingOrder("ascending"); // Default sorting order for a new option is ascending
    }

    setSortingOption(option);

    // Implement the sorting logic based on the selected option and sorting order
    const sortedProducts = [...products];
    if (option === "price") {
      sortedProducts.sort((a, b) => {
        const sortOrderMultiplier = sortingOrder === "ascending" ? 1 : -1;
        return sortOrderMultiplier * (parseFloat(a.price) - parseFloat(b.price));
      });
    } else if (option === "title") {
      sortedProducts.sort((a, b) => {
        const sortOrderMultiplier = sortingOrder === "ascending" ? 1 : -1;
        return sortOrderMultiplier * a.title.localeCompare(b.title);
      });
    }
    dispatch({ type: "UPDATE_PRODUCTS", payload: sortedProducts });
  };

  export default handleSort;
