import React, { useState } from "react";
import { connect } from "react-redux";

import EditProductForm from "../forms/EditProductForm"; // Import the EditProductForm

const ProductDetails = (props) => {
  const { products } = props;
  const [visibleItems, setVisibleItems] = useState(9);
  const [editingProductId, setEditingProductId] = useState(null); // State to track the ID of the product being edited

  const handleEditProduct = (productId) => {
    setEditingProductId(productId);
  };

  const handleExtendList = () => {
    setVisibleItems((prevVisibleItems) => prevVisibleItems + 9);
  };

  return (
    <>
      <ul>
        {products.slice(0, visibleItems).map((product) => (
          <li key={product.id}>
            {/* Display EditProductForm when the product is being edited */}
            {editingProductId === product.id ? (
              <EditProductForm
                product={product}
                onSave={() => setEditingProductId(null)} // Save the edited product and exit edit mode
              />
            ) : (
              <>
                <h2>{product.title}</h2>
                <h3>{product.desc}</h3>
                <p>{product.price}</p>
                <button onClick={() => handleEditProduct(product.id)}>Edit</button>
              </>
            )}
          </li>
        ))}
      </ul>
      {visibleItems < products.length && (
        <button onClick={handleExtendList}>Show More</button>
      )}
    </>
  );
};

const mapStateToProps = (state) => {
  return {
    products: state.products,
  };
};

export default connect(mapStateToProps)(ProductDetails);
