import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { BrowserRouter, Route, Link } from "react-router-dom"; // Import BrowserRouter, Route, and Link
import classes from "./ProductList.module.css";
import EditProductForm from "../forms/EditProductForm";

function App() {
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();

  function fetchProductsHandler() {
    setLoading(true);

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          brand: detailData.brand,
          desc: detailData.description,
          price: detailData.price + "$",
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  return (
    <React.Fragment>
      <section>
        <AddProductForm />
        <button onClick={fetchProductsHandler}>Fetch Products and It's Details</button>
      </section>
      <section>{loading ? <p>Loading...</p> : <ProductDetails />}</section>
    </React.Fragment>
  );
}

export default App;
