import React, { useState } from "react";
import { useDispatch } from "react-redux";

import classes from "./AddForm.module.css";

const AddProductForm = () => {
  const dispatch = useDispatch();
  const [showForm, setShowForm] = useState(false);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [price, setPrice] = useState("");
  const [thumbnail, setThumbnail] = useState(""); // Add thumbnail state

  const handleShowForm = () => {
    setShowForm(true);
  };

  const handleAddProduct = () => {
    if (!title.trim() || !desc.trim() || !parseFloat(price)) {
      alert("Please fill in all the fields.");
      return;
    }

    const newProduct = {
      id: Date.now(),
      title,
      desc,
      price: `${parseFloat(price).toFixed(2)}$`,
      thumbnail: thumbnail || "https://example.com/default-thumbnail.jpg", // Set default thumbnail URL if not provided
    };
    dispatch({ type: "ADD_PRODUCT", payload: newProduct });
    setTitle("");
    setDesc("");
    setPrice("");
    setThumbnail(""); // Clear the thumbnail after adding the product
    setShowForm(false);
  };

  const handleCancel = () => {
    setTitle("");
    setDesc("");
    setPrice("");
    setShowForm(false);
  };

  if (!showForm) {
    return (
      <div>
        <button onClick={handleShowForm}>Add New Product Item</button>
      </div>
    );
  }

  return (
    <div className={classes.form}>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <input
        type="text"
        value={desc}
        onChange={(e) => setDesc(e.target.value)}
        placeholder="Description"
      />
      <input
        type="number"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
        placeholder="Price"
      />
      <input
        type="text"
        value={thumbnail}
        onChange={(e) => setThumbnail(e.target.value)}
        placeholder="Thumbnail URL"
      />
      <div>
        <button onClick={handleAddProduct}>Add</button>
        <button onClick={handleCancel}>Exit</button>
      </div>
    </div>
  );
};

export default AddProductForm;
