import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import AdditionalProductDetailsPage from "./components/AdditionalProductDetailPage";
import ProductDetails from "./components/productDetails";
import AddProductForm from "./forms/AddProductForm";
import SortProducts from "./forms/SortProducts"; // Import the SortProducts component
import "./App.css";

function App() {
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products); // Get products from Redux store

  function fetchProductsHandler() {
    setLoading(true);

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          thumbnail: detailData.thumbnail,
          desc: detailData.description,
          price: detailData.price + "$",
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  return (
    <BrowserRouter>
      <React.Fragment>
        <Routes>
          <Route
            path="/"
            element={
              <ProductListPage
                fetchProductsHandler={fetchProductsHandler}
                loading={loading}
                products={products} // Pass products array as a prop
                dispatch={dispatch} // Pass dispatch function as a prop
              />
            }
          />
          <Route
            path="/product/:id"
            element={<AdditionalProductDetailsPage />}
          />
          <Route path="/product/add" element={<AddProductForm />} />
        </Routes>
      </React.Fragment>
    </BrowserRouter>
  );
}

function ProductListPage({
  fetchProductsHandler,
  loading,
  products,
  handleSort,
  sortingOption,
  sortingOrder,
}) {
  return (
    <section>
      <AddProductForm />
      <button onClick={fetchProductsHandler}>
        Fetch Products and Its Details
      </button>
      {products.length > 0 && ( // Show SortProducts only if products are available
        <SortProducts
          onSort={handleSort}
          sortingOption={sortingOption}
          sortingOrder={sortingOrder}
        />
      )}
      <section className="loading">
        {loading ? <p>Loading...</p> : <ProductDetails />}
      </section>
    </section>
  );
}

export default App;
