import React, { useState } from "react";
import { useDispatch } from "react-redux";

import classes from "./AddForm.module.css";

const AddProductForm = () => {
  const dispatch = useDispatch();
  const [showForm, setShowForm] = useState(false);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [price, setPrice] = useState("");

  const handleShowForm = () => {
    setShowForm(true);
  };

  const handleAddProduct = () => {
    if (!title.trim() || !desc.trim() || !parseFloat(price)) {
      alert("Please fill in all the fields.");
      return;
    }

    const newProduct = {
      title,
      desc,
      price: `${parseFloat(price).toFixed(2)}$`,
    };

    // Simulate an API call to add the new product and get the ID from the response.
    fetch("https://dummyjson.com/add-product", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newProduct),
    })
      .then((response) => response.json())
      .then((data) => {
        // Add the generated ID to the newProduct object.
        newProduct.id = data.id;
        dispatch({ type: "ADD_PRODUCT", payload: newProduct });
        setTitle("");
        setDesc("");
        setPrice("");
        setShowForm(false);
      })
      .catch((error) => {
        console.error("Error adding product:", error);
      });
  };

  const handleCancel = () => {
    setTitle("");
    setDesc("");
    setPrice("");
    setShowForm(false);
  };

  if (!showForm) {
    return (
      <div>
        <button onClick={handleShowForm}>Add New Product Item</button>
      </div>
    );
  }

  return (
    <div className={classes.form}>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <input
        type="text"
        value={desc}
        onChange={(e) => setDesc(e.target.value)}
        placeholder="Description"
      />
      <input
        type="number"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
        placeholder="Price"
      />
      <div>
        <button onClick={handleAddProduct}>Add</button>
        <button onClick={handleCancel}>Exit</button>
      </div>
    </div>
  );
};

export default AddProductForm;
