// components/AdditionalInfo.js
import React, { useEffect, useState } from "react";

const AdditionalInfo = ({ match }) => {
  const productId = parseInt(match.params.id, 10); // Extract the product id from the URL param

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Function to fetch product details from dummyjson.com
    const fetchProductDetails = async () => {
      try {
        const response = await fetch(`https://dummyjson.com/products/${productId}`);
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setProduct(data); // Assuming the API returns the product data directly as an object
        setLoading(false);
      } catch (error) {
        console.error("Error fetching product details:", error);
        setLoading(false);
      }
    };

    fetchProductDetails();
  }, [productId]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!product) {
    return <p>Product not found.</p>;
  }

  return (
    <div>
      <h2>{product.title}</h2>
      <p>{product.description}</p>
      <p>Price: ${product.price}</p>
      <p>Discount: {product.discountPercentage}%</p>
      <p>Rating: {product.rating}</p>
      <p>Brand: {product.brand}</p>
      <p>Category: {product.category}</p>
      <p>Stock: {product.stock}</p>
      {/* Render product images */}
      {product.images.map((image, index) => (
        <img key={index} src={image} alt={`Product Image ${index + 1}`} />
      ))}
    </div>
  );
};

export default AdditionalInfo;
