import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import AdditionalProductDetailsPage from "./components/AdditionalProductDetailPage";
import ProductDetails from "./components/productDetails";
import AddProductForm from "./forms/AddProductForm";
import "./App.css";

function App() {
  const [loading, setLoading] = useState(false);
  const products = useSelector((state) => state.products); // Define the 'products' variable here
  const dispatch = useDispatch();

  function fetchProductsHandler() {
    setLoading(true);

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          thumbnail: detailData.thumbnail,
          desc: detailData.description,
          price: detailData.price + "$",
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  return (
    <BrowserRouter>
      <React.Fragment>
        <Routes>
          <Route
            path="/"
            element={
              <ProductListPage
                fetchProductsHandler={fetchProductsHandler}
                loading={loading}
                products={products} // Pass the 'products' variable as props
              />
            }
          />
          <Route path="/product/:id" element={<AdditionalProductDetailsPage />} />
          <Route path="/product/add" element={<AddProductForm />} />
        </Routes>
      </React.Fragment>
    </BrowserRouter>
  );
}

function ProductListPage({ fetchProductsHandler, loading, products }) {
  return (
    <section>
      <button onClick={fetchProductsHandler}>
        Fetch Products and Its Details
      </button>
      <AddProductForm />
      <section className="loading">{loading ? <p>Loading...</p> : <ProductDetails products={products} />}</section>
    </section>
  );
}

export default App;
