// App.js

import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { BrowserRouter, Route, Routes } from "react-router-dom";

import ProductDetails from "./components/productDetails";
import AddProductForm from "./forms/AddProductForm";
import "./App.css";

function App() {
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();

  useEffect(() => {
    fetchProductsHandler();
  }, []);

  function fetchProductsHandler() {
    setLoading(true);

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          desc: detailData.description,
          price: detailData.price + "$",
          thumbnail: detailData.thumbnail,
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  return (
    <BrowserRouter>
      <nav>
        <ul>
          <li>
            <a href="/">Home</a>
          </li>
          <li>
            <a href="/product/add">Add Product</a>
          </li>
        </ul>
      </nav>
      <Routes>
        <Route
          path="/"
          element={<ProductDetails loading={loading} />}
        />
        <Route
          path="/product/add"
          element={<AddProductForm fetchProductsHandler={fetchProductsHandler} />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
