import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import AdditionalProductDetailsPage from "./components/AdditionalProductDetailPage";
import ProductDetails from "./components/productDetails";
import AddProductForm from "./forms/AddProductForm";
import SortForm from "./forms/SortForm"; // Import SortForm here
import "./App.css";

function App() {
  const [loading, setLoading] = useState(false);
  const [showSortForm, setShowSortForm] = useState(false); // State to control SortForm visibility
  const dispatch = useDispatch();

  function fetchProductsHandler() {
    setLoading(true);
    setShowSortForm(true); // Show SortForm when fetching products

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          thumbnail: detailData.thumbnail,
          desc: detailData.description,
          price: detailData.price + "$",
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  return (
    <BrowserRouter>
      <React.Fragment>
        <Routes>
          <Route
            path="/"
            element={
              <ProductListPage
                fetchProductsHandler={fetchProductsHandler}
                loading={loading}
                showSortForm={showSortForm}
              />
            }
          />
          <Route path="/product/:id" element={<AdditionalProductDetailsPage />} />
          <Route path="/product/add" element={<AddProductForm />} />
        </Routes>
      </React.Fragment>
    </BrowserRouter>
  );
}

function ProductListPage({ fetchProductsHandler, loading, showSortForm }) {
  const handleSort = (option) => {
    if (option === sortingOption) {
      setSortingOrder((prevSortingOrder) =>
        prevSortingOrder === "ascending" ? "descending" : "ascending"
      );
    } else {
      setSortingOrder("ascending");
    }

    setSortingOption(option);

    const sortedProducts = sortProducts(option, sortingOrder);
    dispatch({ type: "UPDATE_PRODUCTS", payload: sortedProducts });
  };

  const sortProducts = (option, order) => {
    const sortedProducts = [...products];
    if (option === "price-asc") {
      sortedProducts.sort((a, b) => {
        const sortOrderMultiplier = order === "ascending" ? 1 : -1;
        return (
          sortOrderMultiplier * (parseFloat(a.price) - parseFloat(b.price))
        );
      });
    } else if (option === "price-desc") {
      sortedProducts.sort((a, b) => {
        const sortOrderMultiplier = order === "ascending" ? -1 : 1;
        return (
          sortOrderMultiplier * (parseFloat(a.price) - parseFloat(b.price))
        );
      });
    } else if (option === "title-asc") {
      sortedProducts.sort((a, b) => {
        const sortOrderMultiplier = order === "ascending" ? 1 : -1;
        return sortOrderMultiplier * a.title.localeCompare(b.title);
      });
    } else if (option === "title-desc") {
      sortedProducts.sort((a, b) => {
        const sortOrderMultiplier = order === "ascending" ? -1 : 1;
        return sortOrderMultiplier * a.title.localeCompare(b.title);
      });
    }

    return sortedProducts;
  };

  return (
    <section>
      <AddProductForm />
      <button onClick={fetchProductsHandler}>
        Fetch Products and Its Details
      </button>
      {showSortForm && <SortForm />} {/* Render SortForm if showSortForm is true */}
      <section className="loading">{loading ? <p>Loading...</p> : <ProductDetails />}</section>
    </section>
  );
}

export default App;
