import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import AdditionalProductDetailsPage from "./components/AdditionalProductDetailPage";
import ProductDetails from "./components/productDetails";
import AddProductForm from "./forms/AddProductForm";
import SortProducts from "./forms/SortProducts"; // Import the SortProducts component
import "./App.css";

function App() {
  const [loading, setLoading] = useState(false);
  const [sortingOption, setSortingOption] = useState("");
  const [sortingOrder, setSortingOrder] = useState("ascending");
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products);

  function fetchProductsHandler() {
    setLoading(true);

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          thumbnail: detailData.thumbnail,
          desc: detailData.description,
          price: detailData.price + "$",
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  const handleSort = (option, order) => {
    // Implement your sorting logic here
    let sortedProducts = [...products];

    if (option === "price-asc") {
      sortedProducts.sort((a, b) => parseFloat(a.price) - parseFloat(b.price));
    } else if (option === "price-desc") {
      sortedProducts.sort((a, b) => parseFloat(b.price) - parseFloat(a.price));
    } else if (option === "title-asc") {
      sortedProducts.sort((a, b) => a.title.localeCompare(b.title));
    } else if (option === "title-desc") {
      sortedProducts.sort((a, b) => b.title.localeCompare(a.title));
    }

    if (order === "descending") {
      sortedProducts.reverse();
    }

    dispatch({ type: "UPDATE_PRODUCTS", payload: sortedProducts });
    setSortingOption(option);
    setSortingOrder(order);
  };

  return (
    <BrowserRouter>
      <React.Fragment>
        <Routes>
          <Route
            path="/"
            element={
              <ProductListPage
                fetchProductsHandler={fetchProductsHandler}
                loading={loading}
                handleSort={handleSort} // Pass handleSort as a prop
                sortingOption={sortingOption} // Pass sortingOption as a prop
                sortingOrder={sortingOrder} // Pass sortingOrder as a prop
              />
            }
          />
          <Route
            path="/product/:id"
            element={<AdditionalProductDetailsPage />}
          />
          <Route path="/product/add" element={<AddProductForm />} />
        </Routes>
      </React.Fragment>
    </BrowserRouter>
  );
}

function ProductListPage({ fetchProductsHandler, loading, handleSort, sortingOption, sortingOrder }) {
  return (
    <section>
      <AddProductForm />
      <button onClick={fetchProductsHandler}>
        Fetch Products and Its Details
      </button>
      <SortProducts
        onSort={handleSort}
        sortingOption={sortingOption}
        sortingOrder={sortingOrder}
      />
      <section className="loading">
        {loading ? <p>Loading...</p> : <ProductDetails />}
      </section>
    </section>
  );
}

export default App;
