import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import classes from "./ProductList.module.css";
import EditProductForm from "../forms/EditProductForm";
import Detail from "./Detail";

const ProductDetails = () => {
  const products = useSelector((state) => state.products);
  const [visibleItems, setVisibleItems] = useState(9);
  const [editingProductId, setEditingProductId] = useState(null);
  const [selectedProductId, setSelectedProductId] = useState(null);

  const dispatch = useDispatch();

  const handleEditProduct = (productId) => {
    if (editingProductId === productId) {
      // Clicking "Cancel" when already editing, revert to previous state
      setEditingProductId(null);
    } else {
      // Clicking "Edit" to start editing
      setEditingProductId(productId);
    }
  };

  const handleDeleteProduct = (productId) => {
    dispatch({ type: "DELETE_PRODUCT", payload: productId });
  };

  const handleExtendList = () => {
    setVisibleItems((prevVisibleItems) => prevVisibleItems + 9);
  };

  const handleProductClick = (productId) => {
    setSelectedProductId(productId);
    setEditingProductId(null);
  };

  return (
    <>
      <ul className={classes.product_list}>
        {products.slice(0, visibleItems).map((product) => (
          <li key={product.id}>
            {selectedProductId === product.id ? (
              <>
                <Detail
                  key={product.id}
                  title={product.title}
                  desc={product.desc}
                  price={product.price}
                />
                {editingProductId === product.id ? (
                  <EditProductForm
                    product={product}
                    onSave={() => setEditingProductId(null)}
                  />
                ) : (
                  <>
                    <button onClick={() => handleEditProduct(product.id)}>
                      {editingProductId === product.id ? "Cancel" : "Edit"}
                    </button>
                    <button onClick={() => handleDeleteProduct(product.id)}>
                      Delete
                    </button>
                  </>
                )}
              </>
            ) : (
              <div onClick={() => handleProductClick(product.id)}>
                {/* Render the product details in a clickable container */}
                <Detail
                  key={product.id}
                  title={product.title}
                  desc={product.desc}
                  price={product.price}
                />
              </div>
            )}
          </li>
        ))}
      </ul>
      {visibleItems < products.length && (
        <button onClick={handleExtendList}>Show More</button>
      )}
    </>
  );
};

export default ProductDetails;
