import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

import AdditionalInfo from "./components/AdditionalInfo";
import ProductDetails from "./components/productDetails";
import AddProductForm from "./forms/AddProductForm";

import "./App.css";

function App() {
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();

  function fetchProductsHandler() {
    setLoading(true);

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          thumbnail: detailData.thumbnail,
          desc: detailData.description,
          price: detailData.price + "$",
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  return (
    <BrowserRouter>
      <React.Fragment>
        <nav>
          <Link to="/">Home</Link>
          {/* Add the "View Product Details" button */}
          <Link to="/additional-info/1">View Product Details</Link>
        </nav>
        <Routes>
          <Route
            path="/"
            element={
              <ProductListPage
                fetchProductsHandler={fetchProductsHandler}
                loading={loading}
              />
            }
          />
          <Route path="/product/:id" element={<ProductDetailPage />} />
          <Route path="/product/add" element={<AddProductFormPage />} />
          {/* Add the route for the AdditionalInfo component */}
          <Route path="/additional-info/:id" element={<AdditionalInfo />} />
        </Routes>
      </React.Fragment>
    </BrowserRouter>
  );
}

function ProductListPage({ fetchProductsHandler, loading }) {
  return (
    <section>
      <AddProductForm />
      <button onClick={fetchProductsHandler}>
        Fetch Products and It's Details
      </button>
      <section>{loading ? <p>Loading...</p> : <ProductDetails />}</section>
    </section>
  );
}

function ProductDetailPage() { 
  return <div>Additional Info</div>;
}

function AddProductFormPage() {
  return <AddProductForm />;
}

export default App;
