import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useDispatch } from "react-redux";
import { Provider } from "react-redux";
import store from "./store";

import ProductDetails from "./components/productDetails";
import AddProductForm from "./forms/AddProductForm";
import "./App.css";

function App() {
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();

  function fetchProductsHandler() {
    setLoading(true);

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          desc: detailData.description,
          price: detailData.price + "$",
          thumbnail: `https://dummyjson.com/${detailData.id}/thumbnail`,
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  return (
    <Provider store={store}>
      <Router>
        <Routes>
          {/* Route for Home */}
          <Route path="/" element={<Home />} />
        </Routes>
      </Router>
    </Provider>
  );
}

function Home() {
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();

  function fetchProductsHandler() {
    setLoading(true);

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          desc: detailData.description,
          price: detailData.price + "$",
          thumbnail: `https://dummyjson.com/${detailData.id}/thumbnail`,
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  return (
    <>
      <AddProductForm />
      <button onClick={fetchProductsHandler}>Fetch Details</button>
      {loading ? <p>Loading...</p> : <ProductDetails />}
    </>
  );
}

export default App;
