import React from "react";

const SortProducts = ({ products, dispatch, handleSort, sortingOption, sortingOrder }) => {
  const updateSorting = (option) => {
    // Check if products are available and not empty before sorting
    if (!products || products.length === 0) {
      return;
    }

    let updatedSortingOrder = "ascending";

    if (option === sortingOption) {
      // Toggle the sorting order if the same option is selected again
      updatedSortingOrder =
        sortingOrder === "ascending" ? "descending" : "ascending";
    }

    let sortedProducts = [...products];

    if (option === "price-asc") {
      sortedProducts.sort((a, b) => parseFloat(a.price) - parseFloat(b.price));
    } else if (option === "price-desc") {
      sortedProducts.sort((a, b) => parseFloat(b.price) - parseFloat(a.price));
    } else if (option === "title-asc") {
      sortedProducts.sort((a, b) => a.title.localeCompare(b.title));
    } else if (option === "title-desc") {
      sortedProducts.sort((a, b) => b.title.localeCompare(a.title));
    }

    if (updatedSortingOrder === "descending") {
      sortedProducts.reverse(); // Reverse the sorted array if sortingOrder is descending
    }

    dispatch({ type: "UPDATE_PRODUCTS", payload: sortedProducts });
    handleSort(option, updatedSortingOrder); // Notify the parent component about the sort action
  };

  return (
    <div>
      <label htmlFor="sort">Sort By:</label>
      <select
        id="sort"
        value={sortingOption}
        onChange={(e) => updateSorting(e.target.value)} // Use updateSorting here
      >
        <option value="">Select an option</option>
        <option value="price-asc">Price - Ascending</option>
        <option value="price-desc">Price - Descending</option>
        <option value="title-asc">Title - Ascending</option>
        <option value="title-desc">Title - Descending</option>
      </select>
    </div>
  );
};

export default SortProducts;
