import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

import AdditionalProductDetailsPage from "./components/AdditionalProductDetailPage";
import ProductDetails from "./components/productDetails";
import AddProductForm from "./forms/AddProductForm";
import SortForm from "./forms/SortForm"; // Add the SortForm import
import "./App.css";

function App() {
  const [loading, setLoading] = useState(false);
  const [fetched, setFetched] = useState(false); // Track whether items are fetched
  const dispatch = useDispatch();

  useEffect(() => {
    // Fetch items only if not fetched already
    if (!fetched) {
      fetchProductsHandler();
    }
  }, [fetchProductsHandler]);

  function fetchProductsHandler() {
    setLoading(true);

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          thumbnail: detailData.thumbnail,
          desc: detailData.description,
          price: detailData.price + "$",
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
        setFetched(true); // Mark items as fetched
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  return (
    <BrowserRouter>
      <React.Fragment>
        {fetched && <SortForm />} {/* Render SortForm after fetching */}
        <Routes>
          <Route
            path="/"
            element={
              <ProductListPage
                loading={loading}
              />
            }
          />
          <Route path="/product/:id" element={<ProductDetailPage />} />
          <Route path="/product/add" element={<AddProductFormPage />} />
        </Routes>
      </React.Fragment>
    </BrowserRouter>
  );
}

function ProductListPage({ loading }) {
  return (
    <section>
      <AddProductForm />
      <section className="loading">{loading ? <p>Loading...</p> : <ProductDetails />}</section>
    </section>
  );
}

function ProductDetailPage() { 
  return <AdditionalProductDetailsPage />;
}

function AddProductFormPage() {
  return <AddProductForm />;
}

export default App;
