import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import classes from "./ProductList.module.css";
import EditProductForm from "../forms/EditProductForm";
import Details from "./Details";

const ProductDetails = () => {
  const products = useSelector((state) => state.products);
  const [visibleItems, setVisibleItems] = useState(9);
  const [editingProductId, setEditingProductId] = useState(null);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [showButtons, setShowButtons] = useState(false);

  const dispatch = useDispatch();
  const buttonsRef = useRef(null);

  useEffect(() => {
    // Add event listener on mount
    document.addEventListener("click", handleClickOutside);

    // Remove event listener on unmount
    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);

  const handleClickOutside = (event) => {
    // Check if the click occurred outside the buttons container
    if (buttonsRef.current && !buttonsRef.current.contains(event.target)) {
      setShowButtons(false);
    }
  };

  const handleEditProduct = (productId) => {
    setEditingProductId(productId);
    setShowButtons(false); // Hide the buttons when editing
  };

  const handleDeleteProduct = (productId) => {
    dispatch({ type: "DELETE_PRODUCT", payload: productId });
  };

  const handleExtendList = () => {
    setVisibleItems((prevVisibleItems) => prevVisibleItems + 9);
  };

  const handleProductClick = (productId) => {
    setSelectedProductId(productId);
    setEditingProductId(null);
    setShowButtons(!showButtons); // Toggle the visibility of buttons
  };

  return (
    <>
      <ul className={classes.product_list}>
        {products.slice(0, visibleItems).map((product) => (
          <li key={product.id}>
            {selectedProductId === product.id ? (
              <>
                <Details
                  key={product.id}
                  title={product.title}
                  thumbnail={product.thumbnail}
                  desc={product.desc}
                  price={product.price}
                />
                {editingProductId === product.id ? (
                  <EditProductForm
                    product={product}
                    onSave={() => setEditingProductId(null)}
                  />
                ) : (
                  <>
                    <div
                      ref={buttonsRef}
                      className={`${classes.buttons} ${
                        showButtons ? classes.visible : classes.hidden
                      }`}
                    >
                      <button onClick={() => handleEditProduct(product.id)}>
                        Edit
                      </button>
                      <button onClick={() => handleDeleteProduct(product.id)}>
                        Delete
                      </button>
                      <Link to={`/product/${product.id}`}>
                        <button>View Details</button>
                      </Link>
                    </div>
                  </>
                )}
              </>
            ) : (
              <div onClick={() => handleProductClick(product.id)}>
                <Details
                  key={product.id}
                  title={product.title}
                  thumbnail={product.thumbnail}
                  desc={product.desc}
                  price={product.price}
                />
              </div>
            )}
          </li>
        ))}
      </ul>
      {visibleItems < products.length && (
        <button onClick={handleExtendList}>Show More</button>
      )}
    </>
  );
};

export default ProductDetails;
