import React, { useState } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { useDispatch } from "react-redux";
import { Provider } from "react-redux";
import store from "./productContext";

import ProductDetails from "./components/productDetails";
import AddProductForm from "./forms/AddProductForm";
import "./App.css";

function App() {
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();

  function fetchProductsHandler() {
    setLoading(true);

    fetch("https://dummyjson.com/products/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        const transformedDetails = data.products.map((detailData) => ({
          id: detailData.id,
          title: detailData.title,
          desc: detailData.description,
          price: detailData.price + "$",
          thumbnail: `https://dummyjson.com/${detailData.id}/thumbnail`,
        }));

        dispatch({ type: "UPDATE_PRODUCTS", payload: transformedDetails });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setLoading(false);
      });
  }

  return (
    <Provider store={store}>
      <Router>
        <React.Fragment>
          <Switch>
            <Route exact path="/">
              <section>
                <AddProductForm />
                <button onClick={fetchProductsHandler}>Fetch Details</button>
              </section>
              <section>
                {loading ? <p>Loading...</p> : <ProductDetails />}
              </section>
            </Route>
            {/* Add other routes here */}
            {/* For example, you can add a route for a "About" page */}
            <Route path="/about">
              <h1>About Page</h1>
              {/* Add content for the About page */}
            </Route>
            {/* You can add more routes as needed */}
          </Switch>
        </React.Fragment>
      </Router>
    </Provider>
  );
}

export default App;
