import React, { useState } from "react";
import { useDispatch } from "react-redux";

const AddProductForm = () => {
  const dispatch = useDispatch();
  const [showForm, setShowForm] = useState(false);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [price, setPrice] = useState(""); // Initialize price as an empty string

  const handleShowForm = () => {
    setShowForm(true);
  };

  const handleAddProduct = () => {
    // Perform form validation
    if (!title.trim() || !desc.trim() || !parseFloat(price)) {
      // Check if price can be parsed to a valid number
      alert("Please fill in all the fields.");
      return;
    }

    const newProduct = {
      id: Date.now(),
      title,
      desc,
      price: `${parseFloat(price).toFixed(2)}$`,
    };
    dispatch({ type: "ADD_PRODUCT", payload: newProduct });

    // Clear the form fields
    setTitle("");
    setDesc("");
    setPrice("");

    setShowForm(false); // Hide the form after adding the product
  };

  const handleCancel = () => {
    // Clear the form fields
    setTitle("");
    setDesc("");
    setPrice("");

    setShowForm(false); // Hide the form and reset the fields
  };

  if (!showForm) {
    return (
      <div>
        <button onClick={handleShowForm}>Add New Product Item</button>
      </div>
    );
  }

  return (
    <div>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <input
        type="text"
        value={desc}
        onChange={(e) => setDesc(e.target.value)}
        placeholder="Description"
      />
      <input
        type="number"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
        placeholder="Price"
      />
      <button onClick={handleAddProduct}>Add</button>
      <button onClick={handleCancel}>Cancel</button>
    </div>
  );
};

export default AddProductForm;
