import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

import classes from "./AdditDet.module.css";

const AdditionalProductDetailsPage = () => {
  const { id } = useParams();
  const [productDetails, setProductDetails] = useState(null);

  useEffect(() => {
    fetch(`https://dummyjson.com/products/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok.");
        }
        return response.json();
      })
      .then((data) => {
        setProductDetails(data);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setProductDetails(null); // Set productDetails to null to trigger the error message display.
      });
  }, [id]);

  if (!productDetails) {
    return <p>Loading product details...</p>;
  }

  // Check if additionalInfo is false and display the "No additional info" message.
  if (productDetails.additionalInfo === false) {
    return <p>No additional info available for this product.</p>;
  }

  return (
    <>
      <div className={classes.product}>
        {/* ...Rest of the code to display product details */}
      </div>
      <div className={classes.go_back}>
        <Link to="/">
          <button>Go Back</button>
        </Link>
      </div>
    </>
  );
};

export default AdditionalProductDetailsPage;
